import type { MediaItem } from "@venuecms/sdk";
import Image, { ImageProps, StaticImageData } from "next/image";

const defaultBlur =
  "data:image/webp;base64,";

export const ResponsiveImage = async ({
  src,
  image = {},
  fallback,
  ...props
}: {
  src?: string;
  image?: Partial<MediaItem>;
  fallback?: StaticImageData;
} & Partial<ImageProps>) => {
  const { altText } = image;
  const metadata: { focus?: { x: number; y: number } } = image?.metadata ?? {};

  const remoteImageProps = src
    ? {
      img: {
        src,
      },
      base64: defaultBlur,
    }
    : {
      img: { src: fallback?.src || defaultBlur },
      base64: defaultBlur,
    };

  // Handle image focusing
  const center = { x: 0.5, y: 0.5 };
  if (metadata?.focus) {
    const y = (-metadata.focus.y + 1) / 2;
    if (y > 0.5) {
      const yWithOffest = y;
      center.y = yWithOffest > 1 ? 1 : yWithOffest;
    } else {
      const yWithOffest = y;
      center.y = yWithOffest < 0 ? 0 : yWithOffest;
    }

    const x = (-metadata.focus.x + 1) / 2;
    if (x > 0.5) {
      const xWithOffest = x;
      center.x = xWithOffest > 1 ? 1 : xWithOffest;
    } else {
      const xWithOffest = x;
      center.x = xWithOffest < 0 ? 0 : xWithOffest;
    }
  }

  return (
    <Image
      alt={(altText as string) ?? "image"}
      src={remoteImageProps.img.src}
      fill={true}
      placeholder="blur"
      blurDataURL={remoteImageProps.base64}
      objectFit="cover"
      objectPosition={`${center.x * 100}% ${center.y * 100}%`}
      {...props}
    />
  );
};

export default ResponsiveImage;
